00493         public void FindObservables(bool force = false)
00494         {
00495             if (!force && this.observableSearch == ObservableSearch.Manual)
00496             {
00497                 return;
00498             }
00499 
00500             if (this.ObservedComponents == null)
00501             {
00502                 this.ObservedComponents = new List<Component>();
00503             }
00504             else
00505             {
00506                 this.ObservedComponents.Clear();
00507             }
00508 
00509             this.transform.GetNestedComponentsInChildren<Component, IPunObservable, PhotonView>(force || this.observableSearch == ObservableSearch.AutoFindAll, this.ObservedComponents);
00510         }
