00703 
00704         private struct CallbackTargetChange
00705         {
00706             public IPhotonViewCallback obj;
00707             public Type type;
00708             public bool add;
00709 
00710             public CallbackTargetChange(IPhotonViewCallback obj, Type type, bool add)
00711             {
00712                 this.obj = obj;
00713                 this.type = type;
00714                 this.add = add;
00715             }
00716         }
00717 
00718         private Queue<CallbackTargetChange> CallbackChangeQueue = new Queue<CallbackTargetChange>();
00719 
00720         private List<IOnPhotonViewPreNetDestroy> OnPreNetDestroyCallbacks;
00721         private List<IOnPhotonViewOwnerChange> OnOwnerChangeCallbacks;
00722         private List<IOnPhotonViewControllerChange> OnControllerChangeCallbacks;
00723 
00728         public void AddCallbackTarget(IPhotonViewCallback obj)
00729         {
00730             CallbackChangeQueue.Enqueue(new CallbackTargetChange(obj, null, true));
00731         }
00732 
00737         public void RemoveCallbackTarget(IPhotonViewCallback obj)
00738         {
00739             CallbackChangeQueue.Enqueue(new CallbackTargetChange(obj, null, false));
00740         }
00741 
00747         public void AddCallback<T>(IPhotonViewCallback obj) where T : class, IPhotonViewCallback
00748         {
00749             CallbackChangeQueue.Enqueue(new CallbackTargetChange(obj, typeof(T), true));
00750         }
00751 
00757         public void RemoveCallback<T>(IPhotonViewCallback obj) where T : class, IPhotonViewCallback
00758         {
00759             CallbackChangeQueue.Enqueue(new CallbackTargetChange(obj, typeof(T), false));
00760         }
00761 
00765         private void UpdateCallbackLists()
00766         {
00767             while (CallbackChangeQueue.Count > 0)
00768             {
00769                 var item = CallbackChangeQueue.Dequeue();
00770                 var obj = item.obj;
00771                 var type = item.type;
00772                 var add = item.add;
00773 
00774                 if (type == null)
00775                 {
00776                     TryRegisterCallback(obj, ref OnPreNetDestroyCallbacks, add);
00777                     TryRegisterCallback(obj, ref OnOwnerChangeCallbacks, add);
00778                     TryRegisterCallback(obj, ref OnControllerChangeCallbacks, add);
00779                 }
00780                 else if (type == typeof(IOnPhotonViewPreNetDestroy))
00781                     RegisterCallback(obj as IOnPhotonViewPreNetDestroy, ref OnPreNetDestroyCallbacks, add);
00782 
00783                 else if (type == typeof(IOnPhotonViewOwnerChange))
00784                     RegisterCallback(obj as IOnPhotonViewOwnerChange, ref OnOwnerChangeCallbacks, add);
00785 
00786                 else if (type == typeof(IOnPhotonViewControllerChange))
00787                     RegisterCallback(obj as IOnPhotonViewControllerChange, ref OnControllerChangeCallbacks, add);
00788             }
00789         }
00790 
00791         private void TryRegisterCallback<T>(IPhotonViewCallback obj, ref List<T> list, bool add) where T : class, IPhotonViewCallback
00792         {
00793             T iobj = obj as T;
00794             if (iobj != null)
00795             {
00796                 RegisterCallback(iobj, ref list, add);
00797             }
00798         }
00799 
00800         private void RegisterCallback<T>(T obj, ref List<T> list, bool add) where T : class, IPhotonViewCallback
00801         {
00802             if (ReferenceEquals(list, null))
00803                 list = new List<T>();
00804 
00805             if (add)
00806             {
00807                 if (!list.Contains(obj))
00808                     list.Add(obj);
00809             }
00810             else
00811             {
00812                 if (list.Contains(obj))
00813                     list.Remove(obj);
00814             }
00815         }
