00211         public Player GetNextFor(int currentPlayerId)
00212         {
00213             if (this.RoomReference == null || this.RoomReference.Players == null || this.RoomReference.Players.Count < 2)
00214             {
00215                 return null;
00216             }
00217 
00218             Dictionary<int, Player> players = this.RoomReference.Players;
00219             int nextHigherId = int.MaxValue;    // we look for the next higher ID
00220             int lowestId = currentPlayerId;     // if we are the player with the highest ID, there is no higher and we return to the lowest player's id
00221 
00222             foreach (int playerid in players.Keys)
00223             {
00224                 if (playerid < lowestId)
00225                 {
00226                     lowestId = playerid;        // less than any other ID (which must be at least less than this player's id).
00227                 }
00228                 else if (playerid > currentPlayerId && playerid < nextHigherId)
00229                 {
00230                     nextHigherId = playerid;    // more than our ID and less than those found so far.
00231                 }
00232             }
00233 
00234             //UnityEngine.Debug.LogWarning("Debug. " + currentPlayerId + " lower: " + lowestId + " higher: " + nextHigherId + " ");
00235             //UnityEngine.Debug.LogWarning(this.RoomReference.GetPlayer(currentPlayerId));
00236             //UnityEngine.Debug.LogWarning(this.RoomReference.GetPlayer(lowestId));
00237             //if (nextHigherId != int.MaxValue) UnityEngine.Debug.LogWarning(this.RoomReference.GetPlayer(nextHigherId));
00238             return (nextHigherId != int.MaxValue) ? players[nextHigherId] : players[lowestId];
00239         }
