00247         protected internal virtual void InternalCacheProperties(Hashtable properties)
00248         {
00249             if (properties == null || properties.Count == 0 || this.CustomProperties.Equals(properties))
00250             {
00251                 return;
00252             }
00253 
00254             if (properties.ContainsKey(ActorProperties.PlayerName))
00255             {
00256                 string nameInServersProperties = (string)properties[ActorProperties.PlayerName];
00257                 if (nameInServersProperties != null)
00258                 {
00259                     if (this.IsLocal)
00260                     {
00261                         // the local playername is different than in the properties coming from the server
00262                         // so the local nickName was changed and the server is outdated -> update server
00263                         // update property instead of using the outdated nickName coming from server
00264                         if (!nameInServersProperties.Equals(this.nickName))
00265                         {
00266                             this.SetPlayerNameProperty();
00267                         }
00268                     }
00269                     else
00270                     {
00271                         this.NickName = nameInServersProperties;
00272                     }
00273                 }
00274             }
00275             if (properties.ContainsKey(ActorProperties.UserId))
00276             {
00277                 this.UserId = (string)properties[ActorProperties.UserId];
00278             }
00279             if (properties.ContainsKey(ActorProperties.IsInactive))
00280             {
00281                 this.IsInactive = (bool)properties[ActorProperties.IsInactive]; //TURNBASED new well-known propery for players
00282             }
00283 
00284             this.CustomProperties.MergeStringKeys(properties);
00285             this.CustomProperties.StripKeysWithNullValues();
00286         }
