00390         public bool SetCustomProperties(Hashtable propertiesToSet, Hashtable expectedValues = null, WebFlags webFlags = null)
00391         {
00392             if (propertiesToSet == null || propertiesToSet.Count == 0)
00393             {
00394                 return false;
00395             }
00396 
00397             Hashtable customProps = propertiesToSet.StripToStringKeys() as Hashtable;
00398 
00399             if (this.RoomReference != null)
00400             {
00401                 if (this.RoomReference.IsOffline)
00402                 {
00403                     if (customProps.Count == 0)
00404                     {
00405                         return false;
00406                     }
00407                     this.CustomProperties.Merge(customProps);
00408                     this.CustomProperties.StripKeysWithNullValues();
00409                     // invoking callbacks
00410                     this.RoomReference.LoadBalancingClient.InRoomCallbackTargets.OnPlayerPropertiesUpdate(this, customProps);
00411                     return true;
00412                 }
00413                 else
00414                 {
00415                     Hashtable customPropsToCheck = expectedValues.StripToStringKeys() as Hashtable;
00416 
00417                     // send (sync) these new values if in online room
00418                     return this.RoomReference.LoadBalancingClient.OpSetPropertiesOfActor(this.actorNumber, customProps, customPropsToCheck, webFlags);
00419                 }
00420             }
00421             if (this.IsLocal)
00422             {
00423                 if (customProps.Count == 0)
00424                 {
00425                     return false;
00426                 }
00427                 if (expectedValues == null && webFlags == null)
00428                 {
00429                     this.CustomProperties.Merge(customProps);
00430                     this.CustomProperties.StripKeysWithNullValues();
00431                     return true;
00432                 }
00433             }
00434 
00435             return false;
00436         }
