00440         private bool SetPlayerNameProperty()
00441         {
00442             if (this.RoomReference != null && !this.RoomReference.IsOffline)
00443             {
00444                 Hashtable properties = new Hashtable();
00445                 properties[ActorProperties.PlayerName] = this.nickName;
00446                 return this.RoomReference.LoadBalancingClient.OpSetPropertiesOfActor(this.ActorNumber, properties);
00447             }
00448 
00449             return false;
00450         }
