00113         string ParseKey(object key)
00114         {
00115             if (key.GetType() == typeof(byte))
00116             {
00117                 byte _byteKey = (byte)key;
00118 
00119                 switch (_byteKey)
00120                 {
00121                     case (byte)255:
00122                         return "PlayerName";
00123                     case (byte)254:
00124                         return "Inactive";
00125                     case (byte)253:
00126                         return "UserId";
00127                 }
00128 
00129             }
