00508     public class PhotonStream
00509     {
00510         private List<object> writeData;
00511         private object[] readData;
00512         private int currentItem; //Used to track the next item to receive.
00513 
00515         public bool IsWriting { get; private set; }
00516 
00518         public bool IsReading
00519         {
00520             get { return !this.IsWriting; }
00521         }
00522 
00524         public int Count
00525         {
00526             get { return this.IsWriting ? this.writeData.Count : this.readData.Length; }
00527         }
00528 
00532         public PhotonStream(bool write, object[] incomingData)
00533         {
00534             this.IsWriting = write;
00535 
00536             if (!write && incomingData != null)
00537             {
00538                 this.readData = incomingData;
00539             }
00540         }
00541 
00542         public void SetReadStream(object[] incomingData, int pos = 0)
00543         {
00544             this.readData = incomingData;
00545             this.currentItem = pos;
00546             this.IsWriting = false;
00547         }
00548 
00549         internal void SetWriteStream(List<object> newWriteData, int pos = 0)
00550         {
00551             if (pos != newWriteData.Count)
00552             {
00553                 throw new Exception("SetWriteStream failed, because count does not match position value. pos: "+ pos + " newWriteData.Count:" + newWriteData.Count);
00554             }
00555             this.writeData = newWriteData;
00556             this.currentItem = pos;
00557             this.IsWriting = true;
00558         }
00559 
00560         internal List<object> GetWriteStream()
00561         {
00562             return this.writeData;
00563         }
00564 
00565 
00566         [Obsolete("Either SET the writeData with an empty List or use Clear().")]
00567         internal void ResetWriteStream()
00568         {
00569             this.writeData.Clear();
00570         }
00571 
00573         public object ReceiveNext()
00574         {
00575             if (this.IsWriting)
00576             {
00577                 Debug.LogError("Error: you cannot read this stream that you are writing!");
00578                 return null;
00579             }
00580 
00581             object obj = this.readData[this.currentItem];
00582             this.currentItem++;
00583             return obj;
00584         }
00585 
00587         public object PeekNext()
00588         {
00589             if (this.IsWriting)
00590             {
00591                 Debug.LogError("Error: you cannot read this stream that you are writing!");
00592                 return null;
00593             }
00594 
00595             object obj = this.readData[this.currentItem];
00596             //this.currentItem++;
00597             return obj;
00598         }
00599 
00601         public void SendNext(object obj)
00602         {
00603             if (!this.IsWriting)
00604             {
00605                 Debug.LogError("Error: you cannot write/send to this stream that you are reading!");
00606                 return;
00607             }
00608 
00609             this.writeData.Add(obj);
00610         }
00611 
00612         [Obsolete("writeData is a list now. Use and re-use it directly.")]
00613         public bool CopyToListAndClear(List<object> target)
00614         {
00615             if (!this.IsWriting) return false;
00616 
00617             target.AddRange(this.writeData);
00618             this.writeData.Clear();
00619 
00620             return true;
00621         }
00622 
00624         public object[] ToArray()
00625         {
00626             return this.IsWriting ? this.writeData.ToArray() : this.readData;
00627         }
00628 
