00857     public class DefaultPool : IPunPrefabPool
00858     {
00860         public readonly Dictionary<string, GameObject> ResourceCache = new Dictionary<string, GameObject>();
00861 
00867         public GameObject Instantiate(string prefabId, Vector3 position, Quaternion rotation)
00868         {
00869             GameObject res = null;
00870             bool cached = this.ResourceCache.TryGetValue(prefabId, out res);
00871             if (!cached)
00872             {
00873                 res = Resources.Load<GameObject>(prefabId);
00874                 if (res == null)
00875                 {
00876                     Debug.LogError("DefaultPool failed to load \"" + prefabId + "\". Make sure it's in a \"Resources\" folder. Or use a custom IPunPrefabPool.");
00877                 }
00878                 else
00879                 {
00880                     this.ResourceCache.Add(prefabId, res);
00881                 }
00882             }
00883 
00884             bool wasActive = res.activeSelf;
00885             if (wasActive) res.SetActive(false);
00886 
00887             GameObject instance =GameObject.Instantiate(res, position, rotation) as GameObject;
00888 
00889             if (wasActive) res.SetActive(true);
00890             return instance;
00891         }
00892 
00895         public void Destroy(GameObject gameObject)
00896         {
00897             GameObject.Destroy(gameObject);
00898         }
00899     }
