00903     public static class PunExtensions
00904     {
00905         public static Dictionary<MethodInfo, ParameterInfo[]> ParametersOfMethods = new Dictionary<MethodInfo, ParameterInfo[]>();
00906 
00907         public static ParameterInfo[] GetCachedParemeters(this MethodInfo mo)
00908         {
00909             ParameterInfo[] result;
00910             bool cached = ParametersOfMethods.TryGetValue(mo, out result);
00911 
00912             if (!cached)
00913             {
00914                 result = mo.GetParameters();
00915                 ParametersOfMethods[mo] = result;
00916             }
00917 
00918             return result;
00919         }
