00354         void Update()
00355         {
00356             if (_lbc!=null) _lbc.Service();
00357 
00358             if (RegionsList !=null)
00359             {
00360                 if (this.ModalWindow.gameObject.activeInHierarchy) {
00361 
00362                     if (PunCockpit.Instance.debug)  Debug.Log("PunCockpit:OnRegionsPinged");
00363 
00364                     this.RegionListView.OnRegionListUpdate (RegionsList);
00365                 }
00366 
00367                 _lbc = null;
00368 
00369                 RegionListLoadingFeedback.text = string.Empty;
00370 
00371                 RegionsList = null;
00372             }
00373         }
