00387         public override void OnRegionListReceived(RegionHandler regionHandler)
00388         {
00389             if (PunCockpit.Instance.debug)
00390                 Debug.Log ("PunCockpit:OnRegionListReceived: " + regionHandler);
00391 
00392             if (_regionPingProcessActive)
00393             {
00394                 RegionListLoadingFeedback.text = "Pinging Regions...";
00395                 _regionPingProcessActive = false;
00396                 regionHandler.PingMinimumOfRegions (OnRegionsPinged, null);
00397             }
00398         }
