00037         public override void OnInspectorGUI()
00038         {
00039             this.m_Target = (PunSceneSettings) this.target;
00040 
00041             // error checking
00042             _duplicateScenesDefinition = m_Target.MinViewIdPerScene.GroupBy(x => x.sceneName)
00043                 .Where(g => g.Count() > 1)
00044                 .Select(y => y.Key)
00045                 .ToList();
00046             
00047             _duplicateViewIdDefinition = m_Target.MinViewIdPerScene.GroupBy(x => x.minViewId)
00048                 .Where(g => g.Count() > 1)
00049                 .Select(y => y.Key)
00050                 .ToList();
00051             
00052             DrawSceneSettingsList();
00053 
00054             foreach (string dup in _duplicateScenesDefinition)
00055             {
00056                 EditorGUILayout.LabelField("Found duplicates for scene",dup);
00057             }
00058             
00059             
00060             foreach (SceneSetting sceneSettings in m_Target.MinViewIdPerScene)
00061             {
00062                 if (_duplicateViewIdDefinition.Contains(sceneSettings.minViewId))
00063                 {
00064                     GUILayout.Label("Found view Id duplicates '"+sceneSettings.minViewId+"' for scene: " +sceneSettings.sceneName);
00065                 }
00066 
00067                 if (sceneSettings.minViewId > PhotonNetwork.MAX_VIEW_IDS)
00068                 {
00069                     GUILayout.Label(sceneSettings.sceneName+" view Id can not exceed the max view Id "+PhotonNetwork.MAX_VIEW_IDS);
00070                 }
00071                 
00072                 if (sceneSettings.minViewId < 1)
00073                 {
00074                     GUILayout.Label(sceneSettings.sceneName+" view Id can not be less than 1");
00075                 }
00076 
00077                 if (sceneSettings.sceneAsset == null && !string.IsNullOrEmpty(sceneSettings.sceneName))
00078                 {
00079                     GUILayout.Label("'"+sceneSettings.sceneName+"' scene is missing in the project");
00080                 }
00081             }
00082             
00083             _firstTime = false;
00084         }
