00147         private void CameraRotation()
00148         {
00149             // if there is an input
00150             if (_input.look.sqrMagnitude >= _threshold)
00151             {
00152                 _cinemachineTargetPitch += _input.look.y * RotationSpeed * Time.deltaTime;
00153                 _rotationVelocity = _input.look.x * RotationSpeed * Time.deltaTime;
00154 
00155                 // clamp our pitch rotation
00156                 _cinemachineTargetPitch = ClampAngle(_cinemachineTargetPitch, BottomClamp, TopClamp);
00157 
00158                 // Update Cinemachine camera target pitch
00159                 CinemachineCameraTarget.transform.localRotation = Quaternion.Euler(_cinemachineTargetPitch, 0.0f, 0.0f);
00160 
00161                 // rotate the player left and right
00162                 transform.Rotate(Vector3.up * _rotationVelocity);
00163             }
