00166         public Vector3 finalMovement;
00167         private void Move()
00168         {
00169             // set target speed based on move speed, sprint speed and if sprint is pressed
00170             float targetSpeed = _input.sprint ? SprintSpeed : MoveSpeed;
00171 
00172             // a simplistic acceleration and deceleration designed to be easy to remove, replace, or iterate upon
00173 
00174             // note: Vector2's == operator uses approximation so is not floating point error prone, and is cheaper than magnitude
00175             // if there is no input, set the target speed to 0
00176             if (_input.move == Vector2.zero) targetSpeed = 0.0f;
00177 
00178             // a reference to the players current horizontal velocity
00179             float currentHorizontalSpeed = new Vector3(_controller.velocity.x, 0.0f, _controller.velocity.z).magnitude;
00180 
00181             float speedOffset = 0.1f;
00182             float inputMagnitude = _input.analogMovement ? _input.move.magnitude : 1f;
00183 
00184             // accelerate or decelerate to target speed
00185             if (currentHorizontalSpeed < targetSpeed - speedOffset || currentHorizontalSpeed > targetSpeed + speedOffset)
00186             {
00187                 // creates curved result rather than a linear one giving a more organic speed change
00188                 // note T in Lerp is clamped, so we don't need to clamp our speed
00189                 _speed = Mathf.Lerp(currentHorizontalSpeed, targetSpeed * inputMagnitude, Time.deltaTime * SpeedChangeRate);
00190 
00191                 // round speed to 3 decimal places
00192                 _speed = Mathf.Round(_speed * 1000f) / 1000f;
00193             }
00194             else
00195             {
00196                 _speed = targetSpeed;
00197             }
00198             _animationBlend = Mathf.Lerp(_animationBlend, targetSpeed, Time.deltaTime * SpeedChangeRate);
00199             // normalise input direction
00200             Vector3 inputDirection = new Vector3(_input.move.x, 0.0f, _input.move.y).normalized;
00201 
00202             // note: Vector2's != operator uses approximation so is not floating point error prone, and is cheaper than magnitude
00203             // if there is a move input rotate player when the player is moving
00204             if (_input.move != Vector2.zero)
00205             {
00206                 // move
00207                 inputDirection = transform.right * _input.move.x + transform.forward * _input.move.y;
00208             }
00209             finalMovement = inputDirection.normalized * (_speed * Time.deltaTime) + new Vector3(0.0f, _verticalVelocity, 0.0f) * Time.deltaTime;
00210             // move the player
00211             _controller.Move(finalMovement);
00212             // update animator if using character
00213             if (_hasAnimator)
00214             {
00215                 _animator.SetFloat(_animIDSpeed, _animationBlend);
00216                 _animator.SetFloat(_animIDMotionSpeed, inputMagnitude);
00217                 _animator.SetFloat("X", _input.move.x* _animationBlend);
00218                 _animator.SetFloat("Z", _input.move.y* _animationBlend);
00219             }
00220         }
