00223         private void JumpAndGravity()
00224         {
00225             if (Grounded)
00226             {
00227                 // reset the fall timeout timer
00228                 _fallTimeoutDelta = FallTimeout;
00229                 // update animator if using character
00230                 if (_hasAnimator)
00231                 {
00232                     _animator.SetBool(_animIDJump, false);
00233                     _animator.SetBool(_animIDFreeFall, false);
00234                 }
00235                 // stop our velocity dropping infinitely when grounded
00236                 if (_verticalVelocity < 0.0f)
00237                 {
00238                     _verticalVelocity = -2f;
00239                 }
00240 
00241 
00242                 // Jump
00243                 if (_input.jump && _jumpTimeoutDelta <= 0.0f && disableJump == false)
00244                 {
00245                     // the square root of H * -2 * G = how much velocity needed to reach desired height
00246 
00247                     _verticalVelocity = Mathf.Sqrt(JumpHeight * -2f * Gravity);
00248                     // update animator if using character
00249                     if (_hasAnimator)
00250                     {
00251                         _animator.SetBool(_animIDJump, true);
00252                     }
00253                 }
00254                 // jump timeout
00255                 if (_jumpTimeoutDelta >= 0.0f)
00256                 {
00257                     _jumpTimeoutDelta -= Time.deltaTime;
00258                 }
00259 
00260             }
00261             else
00262             {
00263                 // reset the jump timeout timer
00264                 _jumpTimeoutDelta = JumpTimeout;
00265 
00266                 // fall timeout
00267                 if (_fallTimeoutDelta >= 0.0f)
00268                 {
00269                     _fallTimeoutDelta -= Time.deltaTime;
00270                 }
00271                 else
00272                 {
00273                     // update animator if using character
00274                     if (_hasAnimator)
00275                     {
00276                         _animator.SetBool(_animIDFreeFall, true);
00277                     }
00278                 }
00279 
00280                 // if we are not grounded, do not jump
00281                 _input.jump = false;
00282             }
00283 
00284             // apply gravity over time if under terminal (multiply by delta time twice to linearly speed up over time)
00285             if (_verticalVelocity < _terminalVelocity)
00286             {
00287                 _verticalVelocity += Gravity * Time.deltaTime;
00288             }
00289         }
