00154     private void CameraRotation()
00155     {
00156         // if there is an input and camera position is not fixed
00157         if (_input.look.sqrMagnitude >= _threshold && !LockCameraPosition)
00158         {
00159             _cinemachineTargetYaw += _input.look.x * Time.deltaTime;
00160             _cinemachineTargetPitch += _input.look.y * Time.deltaTime;
00161         }
00162 
00163         // clamp our rotations so our values are limited 360 degrees
00164         _cinemachineTargetYaw = ClampAngle(_cinemachineTargetYaw, float.MinValue, float.MaxValue);
00165         _cinemachineTargetPitch = ClampAngle(_cinemachineTargetPitch, BottomClamp, TopClamp);
00166 
00167         // Cinemachine will follow this target
00168         CinemachineCameraTarget.transform.rotation = Quaternion.Euler(_cinemachineTargetPitch + CameraAngleOverride, _cinemachineTargetYaw, 0.0f);
00169     }
