00171     private void Move()
00172     {
00173         // set target speed based on move speed, sprint speed and if sprint is pressed
00174         float targetSpeed = _input.sprint ? SprintSpeed : MoveSpeed;
00175 
00176         // a simplistic acceleration and deceleration designed to be easy to remove, replace, or iterate upon
00177 
00178         // note: Vector2's == operator uses approximation so is not floating point error prone, and is cheaper than magnitude
00179         // if there is no input, set the target speed to 0
00180         if (_input.move == Vector2.zero) targetSpeed = 0.0f;
00181 
00182         // a reference to the players current horizontal velocity
00183         float currentHorizontalSpeed = new Vector3(_controller.velocity.x, 0.0f, _controller.velocity.z).magnitude;
00184 
00185         float speedOffset = 0.1f;
00186         float inputMagnitude = _input.analogMovement ? _input.move.magnitude : 1f;
00187 
00188         // accelerate or decelerate to target speed
00189         if (currentHorizontalSpeed < targetSpeed - speedOffset || currentHorizontalSpeed > targetSpeed + speedOffset)
00190         {
00191             // creates curved result rather than a linear one giving a more organic speed change
00192             // note T in Lerp is clamped, so we don't need to clamp our speed
00193             _speed = Mathf.Lerp(currentHorizontalSpeed, targetSpeed * inputMagnitude, Time.deltaTime * SpeedChangeRate);
00194 
00195             // round speed to 3 decimal places
00196             _speed = Mathf.Round(_speed * 1000f) / 1000f;
00197         }
00198         else
00199         {
00200             _speed = targetSpeed;
00201         }
00202         _animationBlend = Mathf.Lerp(_animationBlend, targetSpeed, Time.deltaTime * SpeedChangeRate);
00203 
00204         // normalise input direction
00205         Vector3 inputDirection = new Vector3(_input.move.x, 0.0f, _input.move.y).normalized;
00206 
00207         // note: Vector2's != operator uses approximation so is not floating point error prone, and is cheaper than magnitude
00208         // if there is a move input rotate player when the player is moving
00209         if (_input.move != Vector2.zero)
00210         {
00211             _targetRotation = Mathf.Atan2(inputDirection.x, inputDirection.z) * Mathf.Rad2Deg + _mainCamera.transform.eulerAngles.y;
00212             float rotation = Mathf.SmoothDampAngle(transform.eulerAngles.y, _targetRotation, ref _rotationVelocity, RotationSmoothTime);
00213 
00214             // rotate to face input direction relative to camera position
00215             transform.rotation = Quaternion.Euler(0.0f, rotation, 0.0f);
00216         }
00217 
00218 
00219         Vector3 targetDirection = Quaternion.Euler(0.0f, _targetRotation, 0.0f) * Vector3.forward;
00220 
00221         // move the player
00222         _controller.Move(targetDirection.normalized * (_speed * Time.deltaTime) + new Vector3(0.0f, _verticalVelocity, 0.0f) * Time.deltaTime);
00223 
00224         // update animator if using character
00225         if (_hasAnimator)
00226         {
00227             _animator.SetFloat(_animIDSpeed, _animationBlend);
00228             _animator.SetFloat(_animIDMotionSpeed, inputMagnitude);
00229         }
00230     }
