00232     private void JumpAndGravity()
00233     {
00234         if (Grounded)
00235         {
00236             // reset the fall timeout timer
00237             _fallTimeoutDelta = FallTimeout;
00238 
00239             // update animator if using character
00240             if (_hasAnimator)
00241             {
00242                 _animator.SetBool(_animIDJump, false);
00243                 _animator.SetBool(_animIDFreeFall, false);
00244             }
00245 
00246             // stop our velocity dropping infinitely when grounded
00247             if (_verticalVelocity < 0.0f)
00248             {
00249                 _verticalVelocity = -2f;
00250             }
00251 
00252             // Jump
00253             if (_input.jump && _jumpTimeoutDelta <= 0.0f)
00254             {
00255                 // the square root of H * -2 * G = how much velocity needed to reach desired height
00256                 _verticalVelocity = Mathf.Sqrt(JumpHeight * -2f * Gravity);
00257 
00258                 // update animator if using character
00259                 if (_hasAnimator)
00260                 {
00261                     _animator.SetBool(_animIDJump, true);
00262                 }
00263             }
00264 
00265             // jump timeout
00266             if (_jumpTimeoutDelta >= 0.0f)
00267             {
00268                 _jumpTimeoutDelta -= Time.deltaTime;
00269             }
00270         }
00271         else
00272         {
00273             // reset the jump timeout timer
00274             _jumpTimeoutDelta = JumpTimeout;
00275 
00276             // fall timeout
00277             if (_fallTimeoutDelta >= 0.0f)
00278             {
00279                 _fallTimeoutDelta -= Time.deltaTime;
00280             }
00281             else
00282             {
00283                 // update animator if using character
00284                 if (_hasAnimator)
00285                 {
00286                     _animator.SetBool(_animIDFreeFall, true);
00287                 }
00288             }
00289 
00290             // if we are not grounded, do not jump
00291             _input.jump = false;
00292         }
00293 
00294         // apply gravity over time if under terminal (multiply by delta time twice to linearly speed up over time)
00295         if (_verticalVelocity < _terminalVelocity)
00296         {
00297             _verticalVelocity += Gravity * Time.deltaTime;
00298         }
00299     }
