00301     private static float ClampAngle(float lfAngle, float lfMin, float lfMax)
00302     {
00303         if (lfAngle < -360f) lfAngle += 360f;
00304         if (lfAngle > 360f) lfAngle -= 360f;
00305         return Mathf.Clamp(lfAngle, lfMin, lfMax);
00306     }
