00032         {
00033             get { return (enabled) ? QC_ISPRO ? bits : RoundBitsToBestPreset(bits) : 0; }
00034             set
00035             {
00036                 if (QC_ISPRO)
00037                 {
00038                     bits = value;
00039                     CompressLevel = CompressLevel.SetBits;
00040                 }
00041                 else
00042                 {
00043                     bits = RoundBitsToBestPreset(value);
00044                     CompressLevel = (CompressLevel)bits;
00045                 }
00046 
00047                 if (OnRecalculated != null)
00048                     OnRecalculated(this);
00049             }
00050         }
