00052         [SerializeField] public CompressLevel _compressLevel;
00053         public CompressLevel CompressLevel
00054         {
00055             get { return _compressLevel; }
00056             set
00057             {
00058                 if (QC_ISPRO)
00059                 {
00060                     _compressLevel = value;
00061                     bits = (_compressLevel == CompressLevel.SetBits) ? bits : (int)_compressLevel;
00062                 }
00063                 else
00064                 {
00065                     // If we were using custom bits (moved from Pro to free?), we need to get those bits, and use the closest value we can find
00066                     if (_compressLevel == CompressLevel.SetBits)
00067                         _compressLevel = (CompressLevel)bits;
00068 
00069                     _compressLevel = (CompressLevel)RoundBitsToBestPreset((int)value);
00070                     bits = (int)_compressLevel;
00071                 }
00072 
00073                 if (OnRecalculated != null)
00074                     OnRecalculated(this);
00075             }
00076         }
