00152         public static int RoundBitsToBestPreset(int bits)
00153         {
00154             if (bits > 32)
00155                 return 64;
00156             if (bits > 16)
00157                 return 32;
00158             if (bits > 8)
00159                 return 16;
00160             return 0;
00161         }
