00347         protected internal override void InternalCacheProperties(Hashtable propertiesToCache)
00348         {
00349             int oldMasterId = this.masterClientId;
00350 
00351             base.InternalCacheProperties(propertiesToCache);    // important: updating the properties fields has no way to do callbacks on change
00352 
00353             if (oldMasterId != 0 && this.masterClientId != oldMasterId)
00354             {
00355                 this.LoadBalancingClient.InRoomCallbackTargets.OnMasterClientSwitched(this.GetPlayer(this.masterClientId));
00356             }
00357         }
