00405         public virtual bool SetCustomProperties(Hashtable propertiesToSet, Hashtable expectedProperties = null, WebFlags webFlags = null)
00406         {
00407             if (propertiesToSet == null || propertiesToSet.Count == 0)
00408             {
00409                 return false;
00410             }
00411             Hashtable customProps = propertiesToSet.StripToStringKeys() as Hashtable;
00412 
00413             if (this.isOffline)
00414             {
00415                 if (customProps.Count == 0)
00416                 {
00417                     return false;
00418                 }
00419                 // Merge and delete values.
00420                 this.CustomProperties.Merge(customProps);
00421                 this.CustomProperties.StripKeysWithNullValues();
00422 
00423                 // invoking callbacks
00424                 this.LoadBalancingClient.InRoomCallbackTargets.OnRoomPropertiesUpdate(propertiesToSet);
00425                
00426             }
00427             else
00428             {
00429                 // send (sync) these new values if in online room
00430                 return this.LoadBalancingClient.OpSetPropertiesOfRoom(customProps, expectedProperties, webFlags);
00431             }
00432 
00433             return true;
00434         }
