00605             Hashtable gameProperties = new Hashtable(1);
00606             gameProperties.Add(GamePropertyKey.ExpectedUsers, newExpectedUsers);
00607             Hashtable expectedProperties = null;
00608             if (oldExpectedUsers != null)
00609             {
00610                 expectedProperties = new Hashtable(1);
00611                 expectedProperties.Add(GamePropertyKey.ExpectedUsers, oldExpectedUsers);
00612             }
00613             return this.LoadBalancingClient.OpSetPropertiesOfRoom(gameProperties, expectedProperties);
