00063         public static void DrawAllSettings(Editor targetEditor, bool initializeAsOpen)
00064         {
00065             drawArgs[0] = targetEditor;
00066             drawArgs[3] = initializeAsOpen;
00067 
00068             foreach (var so in registeredSettingsSOs)
00069             {
00070                 // Only show actual assets, not any temp stuff in memory.
00071                 if (!AssetDatabase.Contains(so.Key))
00072                     continue;
00073 
00074                 EditorGUILayout.GetControlRect(false, 4);
00075                 so.Value.Invoke(so.Key, drawArgs);
