00354         public static void RenderContentsOfScriptableObject(SettingsScriptableObjectBase singleton, bool includeScriptField)
00355         {
00356             //EditorGUILayout.Space();
00357 
00358             SerializedObject so = new SerializedObject(singleton);
00359             SerializedProperty sp = so.GetIterator();
00360             sp.Next(true);
00361 
00362             // Skip drawing the script reference?
00363             if (!includeScriptField)
00364                 sp.NextVisible(false);
00365 
