00372             EditorGUILayout.Space();
00373 
00374             if (EditorGUI.EndChangeCheck())
00375             {
00376                 singleton.Initialize();
00377                 so.ApplyModifiedProperties();
00378                 AssetDatabase.SaveAssets();
00379             }
00380         }
