00296     private void CalculateSoundSourceCast(SoundPiece SoundPiece, float MaxVolume, float MaxPitch)
00297     {
00298         if (SoundPiece.source != null && SoundPiece.source.isPlaying && SoundPiece.soundCastSource != null && SoundPiece.soundCastName != "")
00299         {
00300             float _tempVolume = 1f; //Cannot be 0 in this min calculation...
00301             Vector3 totalBalance = Vector3.zero;
00302             int releventPosNum = 0;
00303 
00304             SoundPiece.originalMaxVolume = MaxVolume;
00305             SoundPiece.originalPitch = MaxPitch;
00306             SoundPiece.source.volume = 0f;
00307             foreach (SoundCast SoundCast in SoundPiece.soundCastSource)
00308             {
00309                 float distance = Vector3.Distance(ears.transform.position, SoundCast.soundDynamic.gameObject.transform.position);
00310                 if (distance < SoundCast.soundRange)
00311                 {
00312                     _tempVolume = Mathf.Min(_tempVolume, distance / SoundCast.soundRange);
00313                     totalBalance += SoundCast.soundDynamic.gameObject.transform.position; //Originally, this was gonna be an multiply each, add and divide but that is not needed
00314                     releventPosNum++;
00315                 }
00316             }
00317             SoundPiece.source.volume = (1f - _tempVolume) * SoundPiece.originalMaxVolume;
00318 
00319             Vector3 soundDirection = (totalBalance - ears.transform.position * releventPosNum).normalized;
00320 
00321             if (SoundPiece.soundVisualizerName != null && SoundPiece.soundVisualSource != null)
00322             {
00323                 foreach (SoundVisualizer soundVisualizer in SoundPiece.soundVisualSource)
00324                 {
00325                     soundVisualizer.LastKnownSoundPoint = soundDirection;
00326                 }
00327             }
00328             float DifferenciationY = Vector3.Dot(soundDirection, ears.transform.right);
00329 
00330             SoundPiece.source.panStereo = DifferenciationY;
00331         }
00332 
00333         if (SoundPiece.soundVisualizerName != null && SoundPiece.soundVisualSource != null)
00334         {
00335             foreach (SoundVisualizer soundVisualizer in SoundPiece.soundVisualSource)
00336             {
00337                 float DifferenciationY = Vector3.Dot(soundVisualizer.LastKnownSoundPoint, ears.transform.right);
00338                 float DifferenciationX = Vector3.Dot(soundVisualizer.LastKnownSoundPoint, ears.transform.forward);
00339                 float DifferenciationZ = Vector3.Dot(soundVisualizer.LastKnownSoundPoint, ears.transform.up);
00340                 CalculateVisualPositioning(soundVisualizer, DifferenciationX, DifferenciationY, DifferenciationZ);
00341             }
00342         }
00343 
00344     }
