00446     private void CalculateVisualPositioning(SoundVisualizer soundVisualizer, float fowardBack, float leftRight, float upDown) //Sets a normalized Vector2 for the positional direction the sprite should be within the Canvas screen, after that, a multiplier for how far they should be from the center.
00447     {
00448         Vector2 EncircleVector = new Vector2(leftRight, upDown).normalized;
00449         foreach (SoundVisualAssets soundVisualAssets in soundVisualizer.VisualizerDynamicPart)
00450         {
00451             if (soundVisualAssets.currentTime > 0)
00452             {
00453                 soundVisualAssets.currentTime -= Time.deltaTime;
00454                 Vector2 RealVector = new Vector2(EncircleVector.x * soundVisualAssets.XVisualDistanceFromCenter, EncircleVector.y * soundVisualAssets.YVisualDistanceFromCenter);
00455                 soundVisualAssets.RectImage.localPosition = RealVector;
00456                 soundVisualAssets.ObjectImage.transform.up = RealVector;
00457 
00458                 float a = Mathf.Min(Mathf.Max(1 - fowardBack - soundVisualAssets.VisualAngle, 0) / soundVisualAssets.VisualAngleBuffer, 1); //Calculate Visibility of UI Based on how close you are looking at them.
00459                 float b = Mathf.Min(soundVisualAssets.currentTime / soundVisualAssets.BufferTime, 1); //Calculate Visibility based on how much longer the visual will remain active.
00460                 Color changeVisibility = soundVisualAssets.ComponentImage.color;
00461                 changeVisibility.a = a * b;
00462                 soundVisualAssets.ComponentImage.color = changeVisibility;
00463             }
00464         }
00465         foreach (SoundVisualAssets soundVisualAssets in soundVisualizer.VisualizerStaticPart)
00466         {
00467             if (soundVisualAssets.currentTime > 0)
00468             {
00469                 soundVisualAssets.currentTime -= Time.deltaTime;
00470                 Vector2 RealVector = new Vector2(EncircleVector.x * soundVisualAssets.XVisualDistanceFromCenter, EncircleVector.y * soundVisualAssets.YVisualDistanceFromCenter);
00471                 soundVisualAssets.RectImage.localPosition = RealVector;
00472                 soundVisualAssets.ObjectImage.transform.localPosition = RealVector;
00473 
00474                 float a = Mathf.Min(Mathf.Max(1 - fowardBack - soundVisualAssets.VisualAngle, 0) / soundVisualAssets.VisualAngleBuffer, 1); //Calculate Visibility of UI Based on how close you are looking at them.
00475                 float b = Mathf.Min(soundVisualAssets.currentTime / soundVisualAssets.BufferTime, 1); //Calculate Visibility based on how much longer the visual will remain active.
00476                 Color changeVisibility = soundVisualAssets.ComponentImage.color;
00477                 changeVisibility.a = a * b;
00478                 soundVisualAssets.ComponentImage.color = changeVisibility;
00479             }
00480         }
00481     }
