00614 
00615     IEnumerator playSoundChain(int soundChainID) //An IEnumerator to play the specific sound(Chain, Chunk or Piece) by ID
00616     {
00617         int tempLooper = soundChains[soundChainID].loopNum;
00618         while (tempLooper-- != 0)
00619         {
00620             for (int i = 0; i < soundChains[soundChainID].soundChunks.Length; i++)
00621             {
00622                 int _tempLooper = soundChains[soundChainID].soundChunks[i].loopNum;
00623                 while (_tempLooper-- != 0)
00624                 {
00625                     int rand = Random.Range(0, soundChains[soundChainID].soundChunks[i].soundPieces.Length); //Produce the random instance of the choosen chunk in the chain, picked and used to check for the following array.
00626                     int __tempLooper = soundChains[soundChainID].soundChunks[i].soundPieces[rand].loopNum;
00627                     while (__tempLooper-- != 0)
00628                     {
00629                         yield return soundChains[soundChainID].soundChunks[i].soundPieces[rand].delayBeforeWait; //Delay the sound until as stated duration later, used for timing
00630                         soundChains[soundChainID].soundChunks[i].soundPieces[rand].source.Play(); //Play the attributed sound source
00631                         soundChains[soundChainID].soundChunks[i].soundPieces[rand].ActivateTime(); //Activate any existing visual assets
00632                         UpdateCalculate();
00633                         if (soundChains[soundChainID].soundChunks[i].soundPieces[rand].waitForSoundFinish)
00634                             yield return soundChains[soundChainID].soundChunks[i].soundPieces[rand].delayDuringWait;
00635                         yield return soundChains[soundChainID].soundChunks[i].soundPieces[rand].delayAfterWait;
00636                     }
00637                 }
00638                 //audioSource.PlayOneShot(soundChains[soundChainID].soundChunks[i].soundPieces[rand].Audio, soundChains[soundChainID].Volume * soundChains[soundChainID].soundChunks[i].Volume * soundChains[soundChainID].soundChunks[i].soundPieces[rand].Volume * Volume); //Play the sound stated following the Volume multipliers
00639             }
00640         }
00641     }
00642 
00643     IEnumerator playSoundChunk(int soundChunkID) //An IEnumerator to play the specific sound(Chain, Chunk or Piece) by ID
00644     {
00645         int tempLooper = soundChunks[soundChunkID].loopNum;
