00647         {
00648             int rand = Random.Range(0, soundChunks[soundChunkID].soundPieces.Length); //Produce the random instance of the choosen sound in the chunk, picked and used to check for the following arrays
00649             int _tempLooper = soundChunks[soundChunkID].soundPieces[rand].loopNum;
00650             while (_tempLooper-- != 0)
00651             {
00652                 yield return soundChunks[soundChunkID].soundPieces[rand].delayBeforeWait; //Delay the sound until as stated duration later, used for timing
00653                 soundChunks[soundChunkID].soundPieces[rand].source.Play(); //Play the attributed sound source
00654                 soundChunks[soundChunkID].soundPieces[rand].ActivateTime(); //Activate any existing visual assets
00655                 UpdateCalculate();
00656                 if (soundChunks[soundChunkID].soundPieces[rand].waitForSoundFinish)
00657                     yield return soundChunks[soundChunkID].soundPieces[rand].delayDuringWait;
00658                 yield return soundChunks[soundChunkID].soundPieces[rand].delayAfterWait;
00659             }
00660 
00661             //audioSource.PlayOneShot(soundChunks[soundChunkID].soundPieces[rand].Audio, soundChunks[soundChunkID].Volume * soundChunks[soundChunkID].soundPieces[rand].Volume * Volume); //Play the sound stated following the Volume multipliers
00662 
00663         }
00664     }
