00064     private void Update()
00065     {
00066 
00067         if (!IsMine)
00068             return;
00069 
00070         float t = (Time.time - Time.fixedTime) / Time.fixedDeltaTime;
00071         Interpolate(t);
00072 
00073         if (Input.GetKeyDown(KeyCode.Space))
00074             triggerJump = true;
00075 
00076         if (Input.GetKeyDown(KeyCode.Alpha2))
00077             triggerFade = true;
00078 
00079         if (Input.GetKeyDown(KeyCode.Alpha1))
00080             triggerTurnLeft = true;
00081 
00082 
00083         if (Input.GetKeyDown(KeyCode.Alpha4))
00084         {
00085             if (freakingOut)
00086                 triggerUpperBodyIdle = true;
00087             else
00088                 triggerUpperBodyRun = true;
00089 
00090             freakingOut = !freakingOut;
00091         }
00092 
00093         if (Input.GetKeyDown(KeyCode.F))
00094             triggerProjectile = true;
00095 
00096         if (Input.GetKeyDown(KeyCode.R))
00097             triggerHitscan = true;
00098 
00099         if (Input.GetKeyDown(KeyCode.T))
00100             triggerTeleport = true;
00101 
00102         if (Input.GetKeyDown(KeyCode.B))
00103             triggerBlend = true;
00104     }
