00107     void FixedUpdate()
00108     {
00109 
00110         if (!IsMine)
00111             return;
00112 
00113         Vector3 move = new Vector3(0, 0, 0);
00114         Vector3 turn = new Vector3(0, 0, 0);
00115 
00116 
00117         if (animator && animator.isActiveAndEnabled)
00118         {
00119             if (Input.GetKey(KeyCode.W))
00120             {
00121                 animator.SetBool("walking", true);
00122                 animator.SetFloat("speed", 1);
00123             }
00124             else if (Input.GetKey(KeyCode.S))
00125             {
00126                 animator.SetBool("walking", true);
