00150 
00151             if (triggerFade)
00152             {
00153                 if (syncAnimator)
00154                     syncAnimator.CrossFadeInFixedTime("Jump", .25f);
00155 
00156                 triggerFade = false;
00157             }
00158 
00159             if (triggerBlend)
00160                 animator.SetFloat("blender", Mathf.Abs(Mathf.Sin(Time.time)));
00161             else
00162                 animator.SetFloat("blender", -1);
00163 
00164             if (triggerUpperBodyRun)
00165             {
00166                 if (syncAnimator)
00167                     syncAnimator.SetTrigger("upperBodyRun");
00168 
00169                 triggerUpperBodyRun = false;
00170             }
00171             else if (triggerUpperBodyIdle)
00172             {
00173                 if (syncAnimator)
00174                     syncAnimator.SetTrigger("upperBodyIdle");
00175 
00176                 triggerUpperBodyIdle = false;
00177             }
00178         }
00179 
00180         if (!animator || !animator.applyRootMotion)
00181         {
00182             if (Input.GetKey(KeyCode.W))
00183             {
00184                 move += Vector3.forward;
00185             }
00186             else if (Input.GetKey(KeyCode.S))
00187             {
00188                 move -= Vector3.forward;
00189             }
00190         }
00191 
00192         if (Input.GetKey(KeyCode.A))
00193             move -= Vector3.right;
00194 
00195         if (Input.GetKey(KeyCode.D))
00196             move += Vector3.right;
00197 
00198         if (Input.GetKey(KeyCode.E))
00199             turn += Vector3.up;
00200 
00201         if (Input.GetKey(KeyCode.Q))
00202             turn -= Vector3.up;
00203 
00204         if (Input.touchCount > 0)
00205         {
00206             var touch = Input.GetTouch(0);
00207             Vector2 normTouch = new Vector2(touch.rawPosition.x / Screen.width, touch.rawPosition.y / Screen.height);
00208 
00209             if (normTouch.y > .66f)
00210             {
00211                 if (normTouch.x > .66f)
00212                     triggerHitscan = true;
00213                 else if (normTouch.x < .33f)
00214                     triggerJump = true;
00215             }
00216             else if (normTouch.y < .33f)
00217             {
00218                 if (normTouch.x > .66f)
00219                     move += Vector3.right;
00220                 else if (normTouch.x < .33f)
00221                     move -= Vector3.right;
00222                 else
00223                 {
00224                     if (animator)
00225                     {
00226                         animator.SetBool("walking", true);
00227                         animator.SetFloat("speed", -0.5f);
00228                     }
00229                 }
00230             }
00231             else
00232             {
00233                 if (normTouch.x > .66f)
00234                     turn += Vector3.up;
00235                 else if (normTouch.x < .33f)
00236                     turn -= Vector3.up;
00237                 else
00238                 {
00239                     if (animator)
00240                     {
00241                         animator.SetBool("walking", true);
00242                         animator.SetFloat("speed", 1f);
00243                     }
00244                 }
00245             }
00246         }
00247 
00249         if (autoMove && !Application.isFocused)
00250         {
00251             turn += new Vector3(0, Mathf.Sin(Time.time * .5f), 0);
00252             if (animator)
00253             {
00254                 animator.SetBool("walking", true);
00255                 animator.SetFloat("speed", Mathf.Sin(Time.time) * .5f);
00256             }
00257         }
00258 
00259         Interpolate(1);
00260 
00261         Move(move, turn);
00262 
00263         //Interpolate(1);
00264 
00265         appliedDeltaT = 0;
00266 
00267         if (triggerHitscan)
00268         {
00269             if (syncHitscan)
00270                 syncHitscan.QueueTrigger();
00271 
00272             triggerHitscan = false;
00273         }
00274 
00275         if (triggerProjectile)
00276         {
00277             if (syncLauncher)
00278                 syncLauncher.QueueTrigger();
00279 
00280             triggerProjectile = false;
00281         }
00282 
00283 
00284         if (triggerTeleport)
00285         {
00286             //triggerTeleport = true;
00287             //transform.position = new Vector3(0, 0, 0);
