00301     private void OnAnimatorMove()
00302     {
00303         if (!IsMine)
00304             return;
00305 
00306         animator.ApplyBuiltinRootMotion();
00307 
00308         transform.rotation = animator.rootRotation;
00309         transform.position = animator.rootPosition;
00310     }
00311 
00312 
00313     private void Move(Vector3 move, Vector3 turn)
00314     {
00315         if (is2D)
00316         {
00317             move = new Vector3(move.x, move.z, 0);
00318             turn = new Vector3(0, 0, turn.y);
00319         }
00320 
00321         targRotDelta = turn * turnSpeed * Time.fixedDeltaTime;
00322         targPosDelta = move * moveSpeed * Time.fixedDeltaTime;
00323     }
00324 
00325     void Interpolate(float t)
00326     {
00327         t -= appliedDeltaT;
00328 
00329         appliedDeltaT += t;
00330 
00331         transform.rotation = (transform.rotation * Quaternion.Euler(targRotDelta * t));
00332         transform.position += transform.rotation * (targPosDelta * t);
00333     }
