00074         public LoadBalancingClient Client
00075         {
00076             get { return this.client; }
00077             set
00078             {
00079                 if (this.client != value)
00080                 {
00081                     if (this.client != null)
00082                     {
00083                         this.client.RemoveCallbackTarget(this);
00084                     }
00085                     this.client = value;
00086                     if (this.client != null)
00087                     {
00088                         this.client.AddCallbackTarget(this);
00089                     }
00090                 }
00091             }
00092         }
