00165         private string GetFormattedTimestamp()
00166         {
00167             if (this.startStopwatch == null)
00168             {
00169                 this.startStopwatch = new Stopwatch();
00170                 this.startStopwatch.Start();
00171             }
00172 
00173             TimeSpan span = this.startStopwatch.Elapsed;
00174             if (span.Minutes > 0)
00175             {
00176                 return string.Format("[{0}:{1}.{1}]", span.Minutes, span.Seconds, span.Milliseconds);
00177             }
00178 
00179             return string.Format("[{0}.{1}]", span.Seconds, span.Milliseconds);
00180         }
