00183         // called via InvokeRepeatedly
00184         private void TrackValues()
00185         {
00186             if (this.client != null)
00187             {
00188                 int currentRtt = this.client.LoadBalancingPeer.RoundTripTime;
00189                 if (currentRtt > this.pingMax)
00190                 {
00191                     this.pingMax = currentRtt;
00192                 }
00193                 if (currentRtt < this.pingMin)
00194                 {
00195                     this.pingMin = currentRtt;
00196                 }
00197             }
00198         }
