00204         public void LogStats()
00205         {
00206             if (this.client == null || this.client.State == ClientState.PeerCreated)
00207             {
00208                 return;
00209             }
00210 
00211             if (this.LogTrafficStats)
00212             {
00213                 Debug.Log(string.Format("{0} SupportLogger {1} Ping min/max: {2}/{3}", this.GetFormattedTimestamp() , this.client.LoadBalancingPeer.VitalStatsToString(false) , this.pingMin , this.pingMax));
00214             }
00215         }
