00220         private void LogBasics()
00221         {
00222             if (this.client != null)
00223             {
00224                 List<string> buildProperties = new List<string>(10);
00225                 #if SUPPORTED_UNITY
00226                 buildProperties.Add(Application.unityVersion);
00227                 buildProperties.Add(Application.platform.ToString());
00228                 #endif
00229                 #if ENABLE_IL2CPP
00230                 buildProperties.Add("ENABLE_IL2CPP");
00231                 #endif
00232                 #if ENABLE_MONO
00233                 buildProperties.Add("ENABLE_MONO");
00234                 #endif
00235                 #if DEBUG
00236                 buildProperties.Add("DEBUG");
00237                 #endif
00238                 #if MASTER
00239                 buildProperties.Add("MASTER");
00240                 #endif
00241                 #if NET_4_6
00242                 buildProperties.Add("NET_4_6");
00243                 #endif
00244                 #if NET_STANDARD_2_0
00245                 buildProperties.Add("NET_STANDARD_2_0");
00246                 #endif
00247                 #if NETFX_CORE
00248                 buildProperties.Add("NETFX_CORE");
00249                 #endif
00250                 #if NET_LEGACY
00251                 buildProperties.Add("NET_LEGACY");
00252                 #endif
00253                 #if UNITY_64
00254                 buildProperties.Add("UNITY_64");
00255                 #endif
00256                 #if UNITY_FUSION
00257                 buildProperties.Add("UNITY_FUSION");
00258                 #endif
00259 
00260 
00261                 StringBuilder sb = new StringBuilder();
00262 
00263                 string appIdShort = string.IsNullOrEmpty(this.client.AppId) || this.client.AppId.Length < 8 ? this.client.AppId : string.Concat(this.client.AppId.Substring(0, 8), "***");
00264 
00265                 sb.AppendFormat("{0} SupportLogger Info: ", this.GetFormattedTimestamp());
00266                 sb.AppendFormat("AppID: \"{0}\" AppVersion: \"{1}\" Client: v{2} ({4}) Build: {3} ", appIdShort, this.client.AppVersion, PhotonPeer.Version, string.Join(", ", buildProperties.ToArray()), this.client.LoadBalancingPeer.TargetFramework);
00267                 if (this.client != null && this.client.LoadBalancingPeer != null && this.client.LoadBalancingPeer.SocketImplementation != null)
00268                 {
00269                     sb.AppendFormat("Socket: {0} ", this.client.LoadBalancingPeer.SocketImplementation.Name);
00270                 }
00271 
00272                 sb.AppendFormat("UserId: \"{0}\" AuthType: {1} AuthMode: {2} {3} ", this.client.UserId, (this.client.AuthValues != null) ? this.client.AuthValues.AuthType.ToString() : "N/A", this.client.AuthMode, this.client.EncryptionMode);
00273 
00274                 sb.AppendFormat("State: {0} ", this.client.State);
00275                 sb.AppendFormat("PeerID: {0} ", this.client.LoadBalancingPeer.PeerID);
00276                 sb.AppendFormat("NameServer: {0} Current Server: {1} IP: {2} Region: {3} ", this.client.NameServerHost, this.client.CurrentServerAddress, this.client.LoadBalancingPeer.ServerIpAddress, this.client.CloudRegion);
00277 
00278                 Debug.LogWarning(sb.ToString());
00279             }
00280         }
