00283         public void OnConnected()
00284         {
00285             Debug.Log(this.GetFormattedTimestamp() + " SupportLogger OnConnected().");
00286             this.pingMax = 0;
00287             this.pingMin = this.client.LoadBalancingPeer.RoundTripTime;
00288             this.LogBasics();
00289 
00290             if (this.LogTrafficStats)
00291             {
00292                 this.client.LoadBalancingPeer.TrafficStatsEnabled = false;
00293                 this.client.LoadBalancingPeer.TrafficStatsEnabled = true;
00294                 this.StartLogStats();
00295             }
00296 
00297             this.StartTrackValues();
00298         }
