00086         private void AddVector()
00087         {
00089             float delta = DoubleTime.mixedDeltaTime;
00090 
00092             transform.localScale += (sclDef.addVector * delta);
00093 
00095             if (rotDef.local)
00096                 transform.localEulerAngles += rotDef.addVector * delta;
00097             else
00098                 transform.eulerAngles += rotDef.addVector * delta;
00099             //transform.Rotate(rotDef.addVector * mixedDelta, rotDef.local ? Space.Self : Space.World);
00100 
00102             transform.Translate(posDef.addVector * delta, posDef.local ? Space.Self : Space.World);
00103         }
