00124         public void AutoSetSyncTransformEnablesAdditive()
00125         {
00126             var syncTransform = GetComponent<SyncTransform>();
00127 
00128             var st = syncTransform;
00129 
00130             // Position
00131             {
00132                 var def = posDef;
