00134                 bool local = def.local;
00135 
00136                 c.local = local;
00137 
00138                 if (local)
00139                 {
00140                     bool iszero = def.addVector.magnitude == 0;
00141                     c.XCrusher.Enabled = !iszero;
00142                     c.YCrusher.Enabled = !iszero;
00143                     c.ZCrusher.Enabled = !iszero;
00144                 }
00145                 else
00146                 {
00147                     var addVector = def.addVector;
00148                     c.XCrusher.Enabled = addVector.x != 0;
00149                     c.YCrusher.Enabled = addVector.y != 0;
00150                     c.ZCrusher.Enabled = addVector.z != 0;
00151                 }
00152             }
00153 
00155             {
00156                 var def = rotDef;
00157                 var c = st.transformCrusher.RotCrusher;
00158                 bool local = def.local;
00159 
00160                 c.local = local;
00161                 bool iszero = def.addVector.magnitude == 0;
00162 
00163                 if (iszero || !rotDef.local)
00164                 {
00165                     c.TRSType = TRSType.Quaternion;
00166                     c.QCrusher.Enabled = !iszero;
00167                 }
00168                 else
00169                 {
00170                     c.TRSType = TRSType.Euler;
00171                     var addVector = def.addVector;
00172 
00173                     c.XCrusher.Enabled = local ? addVector.x != 0 : !iszero;
00174                     c.YCrusher.Enabled = local ? addVector.y != 0 : !iszero;
00175                     c.ZCrusher.Enabled = local ? addVector.z != 0 : !iszero;
00176                 }
00177             }
00178 
00179 
00181             {
00182                 var def = sclDef;
00183                 var c = st.transformCrusher.SclCrusher;
00184 
00185                 bool usescl = (sclDef.addVector.sqrMagnitude != 0);
00186                 if (usescl)
00187                 {
00188                     bool iszero = def.addVector.magnitude == 0;
00189 
00190                     bool local = def.local;
00191                     var addVector = sclDef.addVector;
00192                     c.uniformAxes = ElementCrusher.UniformAxes.NonUniform;
00193                     c.XCrusher.Enabled = local ? addVector.x != 0 : !iszero;
00194                     c.YCrusher.Enabled = local ? addVector.y != 0 : !iszero;
00195                     c.ZCrusher.Enabled = local ? addVector.z != 0 : !iszero;
00196                     c.local = def.local;
00197                 }
00198                 else
00199                 {
00200                     c.uniformAxes = ElementCrusher.UniformAxes.XYZ;
00201                     c.UCrusher.Enabled = false;
