00312         private static Dictionary<object, bool> foldoutStates = new Dictionary<object, bool>();
00313         protected void ListFoundInterfaces<T>(GameObject go, List<T> list) where T : class
00314         {
00315             go.GetComponentsInChildren(list);
00316 
00317             reusableGC.text = "[" + list.Count + "] " + typeof(T).Name + " found:";
