00323             foldoutStates[list] = EditorGUILayout.Foldout(foldoutStates[list], reusableGC);
00324 
00325             if (foldoutStates[list])
00326             {
00327                 EditorGUILayout.BeginVertical(new GUIStyle("HelpBox"));
00328 
00329                 if (list.Count == 0)
00330                     EditorGUILayout.LabelField("<i>none</i>", richBox);
