00332                 {
00333                     EditorGUI.BeginDisabledGroup(true);
00334                     for (int i = 0; i < list.Count; ++i)
00335                     {
00336                         EditorGUILayout.ObjectField(list[i] as Component, typeof(Component), false);
00337                     }
