00078         public void OnCaptureCurrentState(int frameId)
00079         {
00080 
00081             Frame frame = frames[frameId];
00082 
00083             if (pendingOwnerChange)
00084             {
00085 
00086                 //Debug.Log(Time.time + " fid: " + frameId + " CAP OWNER " + pendingOwnerId);
00087                 var photonView = this.photonView;
00088 
00089                 // If we are master and we are taking control of scene objects, immediately change owner. Otherwise we are just setting the new owner as controller and awaiting them to take over.
00090                 if (photonView.OwnerActorNr != 0)
00091                 {
00092                     ticksUntilOwnershipRetry = TickEngineSettings.frameCount;
00093                 }
00094 
00095                 NetMasterCallbacks.postCallbackActions.Enqueue(DeferredOwnerChange);
00096 
00097                 frame.ownerActorId = pendingOwnerId;
00098                 frame.ownerHasChanged = true;
00099                 pendingOwnerChange = false;
00100 
00101             }
00102             else
00103             {
00104                 frames[frameId].ownerActorId = photonView.OwnerActorNr;
00105             }
00106         }
